import { Injectable, OnModuleInit } from "@nestjs/common";
import { EnvService } from "../env/env.service";

export type BrandChartStyling = {
    companyImage: string;
    colors: {
        primary: string;
        secondary?: string;
        background: string;
        card: string;
        text: string;
        border: string;
        notification: string;
    };
};

export type PinProfile = {
    email: string;
    pin: string;
    companyId: string;
    brand: BrandChartStyling;
};

@Injectable()
export class PinService implements OnModuleInit {
    private readonly pinStore = new Map<string, PinProfile>();

    constructor(private readonly envService: EnvService) { }

    onModuleInit() {
        this.pinStore.set(this.envService.userAuthEmail(), {
            email: this.envService.userAuthEmail(),
            pin: '0000',
            companyId: '1',
            brand: {
                companyImage: 'data:image/png;base64,',
                colors: {
                    primary: '#FEE12B',
                    secondary: '#CCCCCC',
                    background: '#E6E6E6',
                    card: '#666666',
                    text: '#1C1C1C',
                    border: '#FFFDD0',
                    notification: '#E3B778',
                },
            },
        });

        this.pinStore.set(this.envService.userAuthEmail('1'), {
            email: this.envService.userAuthEmail('1'),
            pin: '1234',
            companyId: "2",
            brand: {
                companyImage: 'data:image/png;base64,',
                colors: {
                    primary: '#4285F4',
                    background: '#FFFFFF',
                    card: '#F1F3F4',
                    text: '#202124',
                    border: '#DADCE0',
                    notification: '#FF9800',
                },
            },
        });

        this.pinStore.set(this.envService.userAuthEmail('2'), {
            email: this.envService.userAuthEmail('2'),
            pin: '0000',
            companyId: '1',
            brand: {
                companyImage: 'data:image/png;base64,',
                colors: {
                    primary: '#ABE4A0',
                    secondary: '#F5F5F7',
                    background: '#FFFFFF',
                    card: '#F1F3F4',
                    text: '#000000',
                    border: '#D1D5DB',
                    notification: '#4B5320',
                },
            },
        });

        this.pinStore.set(this.envService.userAuthEmail('3'), {
            email: this.envService.userAuthEmail('3'),
            pin: '0000',
            companyId: '2',
            brand: {
                companyImage: 'data:image/jpeg;base64,',
                colors: {
                    primary: '#FEE12B',
                    secondary: '#CCCCCC',
                    background: '#E6E6E6',
                    card: '#666666',
                    text: '#1C1C1C',
                    border: '#FFFDD0',
                    notification: '#E3B778',
                },
            },
        });
    }

    async verifyPin(email: string, pin: string): Promise<{ isValidPin: boolean, brand: BrandChartStyling | undefined, companyId: string | undefined }> {
        const storedPin = this.pinStore.get(email);
        console.log(`Verifying PIN for ${email}: storedPin=${storedPin?.pin}, providedPin=${pin}`);

        return { isValidPin: storedPin?.pin === pin, brand: storedPin?.brand, companyId: storedPin?.companyId };
    }

}